    private void jButton2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton2MouseClicked
        if(fn.getText().isEmpty() 
                || ln.getText().isEmpty()
                || un.getText().isEmpty()
                || em.getText().isEmpty()
                || pw.getText().isEmpty()
                || cp.getText().isEmpty()){
            JOptionPane.showMessageDialog(null, "All fields are required!", "Error", JOptionPane.ERROR_MESSAGE);
        }
        else {
            // Validate email format
            String email = em.getText();
            String emailRegex = "^[A-Za-z0-9+_.-]+@[A-Za-z0-9.-]+$";
                
            if (!email.matches(emailRegex)) {
               JOptionPane.showMessageDialog(null, "Invalid email format!", "Error", JOptionPane.ERROR_MESSAGE);
               } 
                // Validate password match
            else if (!pw.getText().equals(cp.getText())) {
                JOptionPane.showMessageDialog(null, "Passwords do not match!", "Error", JOptionPane.ERROR_MESSAGE);
            } 
                // Validate password strength
            else {
                String password = pw.getText();
                String passwordRegex = "^(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[@#$%^&+=!]).{8,}$";

            if (!password.matches(passwordRegex)) {
                JOptionPane.showMessageDialog(null, "Password must be at least 8 characters and include:\n- One uppercase letter\n- One lowercase letter\n- One number\n- One special character (@#$%^&+=!)", 
                                              "Error", JOptionPane.ERROR_MESSAGE);
               } 
            else {
                // Insert into database
                connectDB con = new connectDB();
                int result = con.InsertData("INSERT INTO users (first_name, last_name, username, email, password, role) "
                       + "VALUES('"+fn.getText()+"',"
                               + "'"+ln.getText()+"',"
                               + "'"+un.getText()+"',"
                               + "'"+em.getText()+"',"
                               + "'"+pw.getText()+"',"
                               + "'"+rl.getSelectedItem()+"')");
                   if (result == 1) {
                       JOptionPane.showMessageDialog(null, "Inserted Successfully!");
                       new login().setVisible(true);
                       this.dispose();
                   } 
                   else {
                       JOptionPane.showMessageDialog(null, "Registration failed. Please try again.", "Error", JOptionPane.ERROR_MESSAGE);
                   }
               }
           }
       }
    }//GEN-LAST:event_jButton2MouseClicked

